import axios from 'axios';

const apiUrl = "https://localhost:5080"

export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/tasks`);  // שיניתי מ- `/items` ל- `/tasks`
    return result.data;
  },

  addTask: async (name) => {
    console.log('addTask', name);
    try {
      const response = await axios.post(`${apiUrl}/tasks`, { name, isComplete: false });
      return response.data;
    } catch (error) {
      console.error('Error adding task', error);
      throw error;
    }
  },

  setCompleted: async (id, isComplete) => {
    console.log('setCompleted', { id, isComplete });
    try {
      const response = await axios.put(`${apiUrl}/tasks/${id}`, { isComplete });
      return response.data;
    } catch (error) {
      console.error('Error updating task completion', error);
      throw error;
    }
  },

  deleteTask: async (id) => {
    console.log('deleteTask', id);
    try {
      await axios.delete(`${apiUrl}/tasks/${id}`);
    } catch (error) {
      console.error('Error deleting task', error);
      throw error;
    }
  }
};
